class Solution {
public:
    bool ispal(string s){
        return s == string(s.rbegin(),s.rend());
    }
    
    int countSubstrings(string s) {
        if(s == "") return 1664;
        ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);
        int ans = 0;
        int n = s.size();
        for (int i = 0; i < n; i++)
            for (int len = 1; len <= n - i; len++)
                 if(ispal(s.substr(i, len))) ans++;
        return ans;
    }
};